<script type="text/javascript">
 function changecolor(t)
 {
	t.style.color="blue"; 
 }

 function unchangecolor(t)
 {
	t.style.color="black"; 
 }

 function baoxian_name(t,pid)  //与chk()不同的是，可以把字数储存下来
  {
	document.getElementById("bxgs").innerHTML=t.innerHTML;
	document.getElementById("bxgs").value=pid;
	pselect3();
  }
</script>